"use strict";

const express = require("express");
const cors = require("cors");
const lib_1 = require("./lib").default;

const app = express();

// ✅ Allow only specific frontend domains (Replace with your actual frontend URL)
const allowedOrigins = ["https://adrean-25-1.onrender.com"];

app.use(cors({
    origin: function (origin, callback) {
        if (!origin || allowedOrigins.includes(origin)) {
            callback(null, true);
        } else {
            callback(new Error("Not allowed by CORS"));
        }
    },
    methods: "GET, POST, PUT, DELETE, OPTIONS",
    allowedHeaders: "Content-Type, Authorization"
}));

app.use(express.json()); // Parse JSON requests

// ✅ Handle preflight (OPTIONS) requests
app.options("*", (req, res) => {
    res.header("Access-Control-Allow-Origin", "https://adrean-25-1.onrender.com");
    res.header("Access-Control-Allow-Methods", "GET, POST, PUT, DELETE, OPTIONS");
    res.header("Access-Control-Allow-Headers", "Content-Type, Authorization");
    res.sendStatus(200);
});

// Example route using your library
app.post("/send-wish", (req, res) => {
    res.json({ message: "Wish sent successfully!" });
});

const PORT = process.env.PORT || 3000;
app.listen(PORT, () => console.log(`Server running on port ${PORT}`));

module.exports = app;
